(function() {
  describe('jasmine-before-all', function() {
    describe('intentional pollution', function() {
      var foo;
      foo = 0;
      beforeAll(function() {
        return foo += 1;
      });
      context('1', function() {
        return Then(function() {
          return foo === 1;
        });
      });
      context('2', function() {
        return Then(function() {
          return foo === 1;
        });
      });
      context('3', function() {
        return Then(function() {
          return foo === 1;
        });
      });
      return afterAll(function() {
        foo++;
        return expect(foo).toEqual(2);
      });
    });
    return describe("exploding occurring when afterAll is called twice", function() {
      var callCount;
      callCount = 0;
      afterAll(function() {
        callCount++;
        return expect(callCount).toEqual(1);
      });
      Then(function() {
        return true;
      });
      Then(function() {
        return true;
      });
      return describe("foo", function() {
        Then(function() {
          return true;
        });
        return Then(function() {
          return true;
        });
      });
    });
  });

}).call(this);
